sys_script_include.script:
var PrefProps = Class.create();
PrefProps.prototype = (function ProfProps_proto(s) {
	var TYPE = 'PrefProps';
	var p = {};

	function getScopePrefix(scope) {
		if (!scope || scope === 'global' || scope === 'rhino.global') {
			return '';
		}
		return scope + '.';
	}
	s.getScopePrefix = getScopePrefix;

	function initialize(scope, middle) {
		var prefix;

		scope = scope || gs.getCallerScopeName();
		scope = getScopePrefix(gs.getCallerScopeName());

		if (middle !== undefined) {
			prefix = scope + middle + '.';
		} else {
			prefix = scope;
		}

		this.getPrefix = function getPrefix() { return prefix; };

		function getName(name) {
			return prefix + name;
		}

		// Get property or user preference.
		this.getValue = function getValue(name, defaultValue) {
			var fullName, user, value;

			fullName = getName(name);

			user = gs.getUser();
			value = user.getPreference(fullName);
			if (value === null) {
				value = gs.getProperty(fullName, defaultValue);
				user.savePreference(fullName, value);
			}
			return value;
		};

	} // End initialize
	p.initialize = initialize;

	return p;
})(PrefProps); // End ProfProps_proto
